import React from 'react';
import { View, Text, StyleSheet, ScrollView } from 'react-native';

const MenuScreen = () => {
  const days = {
    "Sunday": {
      "Breakfast": ["Greek yogurt with mixed berries", "sprinkle of granola"],
      "Lunch": ["Grilled chicken salad", "mixed greens", "cherry tomatoes", "cucumber", "balsamic vinaigrette"],
      "Snacks": ["Baby carrots", "hummus"],
      "Dinner": ["Baked salmon", "quinoa", "steamed broccoli"]
    },
    "Monday": {
      "Breakfast": ["Oatmeal with sliced banana", "spoonful of almond butter"],
      "Lunch": ["Turkey and avocado wrap", "whole wheat tortilla", "lettuce", "mustard"],
      "Snacks": ["Apple slices", "handful of almonds"],
      "Dinner": ["Stir-fried tofu", "brown rice", "stir-fried vegetables (bell peppers, snap peas, carrots)", "light soy sauce"]
    },
    "Tuesday": {
      "Breakfast": ["Scrambled eggs with spinach", "feta cheese"],
      "Lunch": ["Quinoa salad", "black beans", "corn", "red onion", "lime vinaigrette"],
      "Snacks": ["Greek yogurt", "honey"],
      "Dinner": ["Grilled lean steak", "sweet potato", "side salad"]
    },
    "Wednesday": {
      "Breakfast": ["Whole-grain toast with avocado", "poached egg"],
      "Lunch": ["Lentil soup", "side of whole-grain crackers"],
      "Snacks": ["Mixed nuts (almonds, walnuts, cashews)"],
      "Dinner": ["Grilled chicken breast", "roasted Brussels sprouts", "quinoa"]
    },
    "Thursday": {
      "Breakfast": ["Smoothie with spinach", "banana", "almond milk", "scoop of protein powder"],
      "Lunch": ["Chickpea and vegetable wrap", "tahini sauce", "whole wheat tortilla"],
      "Snacks": ["Cottage cheese", "pineapple chunks"],
      "Dinner": ["Baked cod", "asparagus", "quinoa pilaf"]
    },
    "Friday": {
      "Breakfast": ["Greek yogurt parfait", "granola", "sliced strawberries"],
      "Lunch": ["Grilled vegetable and mozzarella panini", "whole-grain bread"],
      "Snacks": ["Rice cakes", "almond butter"],
      "Dinner": ["Shrimp stir-fry", "brown rice", "mixed vegetables", "teriyaki sauce"]
    },
    "Saturday": {
      "Breakfast": ["Whole-grain pancakes", "mixed berries"],
      "Lunch": ["Spinach and feta stuffed chicken breast", "steamed broccoli"],
      "Snacks": ["Rice crackers", "guacamole"],
      "Dinner": ["Lentil and vegetable curry", "brown rice"]
    }
  };

  const getCurrentDayMenu = () => {
    const currentDayIndex = new Date().getDay();
    const currentDayName = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'][currentDayIndex];
    console.log(currentDayName, currentDayIndex);
    return days[currentDayName];
  };

  const currentDayMenu = getCurrentDayMenu();

  return (
    <ScrollView contentContainerStyle={styles.container}>
      {Object.keys(currentDayMenu).map((mealType, index) => (
        <MenuDay key={index} day={mealType} meals={currentDayMenu[mealType]} />
      ))}
    </ScrollView>
  );
};

const MealItem = ({ item }) => (
  <View style={styles.mealContainer}>
    <Text style={styles.mealText}>{item}</Text>
  </View>
);

const MenuDay = ({ day, meals }) => (
  <View style={styles.dayContainer}>
    <Text style={styles.dayText}>{day}:</Text>
    {meals.map((meal, index) => (
      <MealItem key={index} item={meal} />
    ))}
  </View>
);

const styles = StyleSheet.create({
  container: {
    padding: 20,
    backgroundColor: '#F5F5F5',
  },
  dayContainer: {
    marginBottom: 20,
  },
  dayText: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  mealContainer: {
    backgroundColor: '#FFFFFF',
    padding: 10,
    marginBottom: 5,
    borderWidth: 1,
    borderColor: '#DDDDDD',
    borderRadius: 5,
  },
  mealText: {
    fontSize: 16,
  },
});

export default MenuScreen;
