import React from 'react';
import { View, StyleSheet, TouchableOpacity, Text } from 'react-native';
import Icon from 'react-native-vector-icons/Ionicons';

const HomeScreen = () => {
  const iconData = [
    { name: 'person-circle-outline', description: 'Profile' },
    { name: 'chatbubble-ellipses-outline', description: 'Messages' },
    { name: 'camera-outline', description: 'Camera' },
    { name: 'notifications-outline', description: 'Notifications' },
  ];

  const renderIconBox = (iconName, description) => (
    <TouchableOpacity style={styles.iconBox}>
      <Icon name={iconName} size={50} color="#4285F4" />
      <Text style={styles.description}>{description}</Text>
    </TouchableOpacity>
  );

  const renderIconGrid = () => (
    <View style={styles.iconGrid}>
      {iconData.map((data, index) => (
        <View key={index} style={styles.iconBoxContainer}>
          {renderIconBox(data.name, data.description)}
        </View>
      ))}
    </View>
  );

  return (
    <View style={styles.container}>
      {renderIconGrid()}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#F5F5F5',
  },
  iconGrid: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'space-between',
    paddingHorizontal: 20,
    marginTop: 20,
  },
  iconBoxContainer: {
    width: '48%',
    marginBottom: 20,
  },
  iconBox: {
    width: '100%',
    height: 150,
    backgroundColor: '#FFFFFF',
    borderRadius: 15,
    justifyContent: 'center',
    alignItems: 'center',
    elevation: 5,
  },
  description: {
    marginTop: 10,
    fontSize: 18,
    fontWeight: 'bold',
    color: '#333333',
  },
});

export default HomeScreen;
