import React from 'react';
import { View, Text, StyleSheet, ScrollView } from 'react-native';

const MenuScreen = () => {
    const days = [
        {
          day: 'Sunday',
          meals: [
            'Breakfast: Greek yogurt with mixed berries and a sprinkle of granola.',
            'Lunch: Grilled chicken salad with mixed greens, cherry tomatoes, cucumber, and balsamic vinaigrette.',
            'Snack: Baby carrots with hummus.',
            'Dinner: Baked salmon with quinoa and steamed broccoli.',
          ],
        },
        {
          day: 'Monday',
          meals: [
            'Breakfast: Oatmeal with sliced banana and a spoonful of almond butter.',
            'Lunch: Turkey and avocado wrap with whole wheat tortilla, lettuce, and mustard.',
            'Snack: Apple slices with a handful of almonds.',
            'Dinner: Stir-fried tofu with brown rice and stir-fried vegetables (bell peppers, snap peas, carrots) in a light soy sauce.',
          ],
        },
        {
          day: 'Tuesday',
          meals: [
            'Breakfast: Scrambled eggs with spinach and feta cheese.',
            'Lunch: Quinoa salad with black beans, corn, red onion, and a lime vinaigrette.',
            'Snack: Greek yogurt with honey.',
            'Dinner: Grilled lean steak with sweet potato and a side salad.',
          ],
        },
        {
          day: 'Wednesday',
          meals: [
            'Breakfast: Whole-grain toast with avocado and a poached egg.',
            'Lunch: Lentil soup with a side of whole-grain crackers.',
            'Snack: Mixed nuts (almonds, walnuts, cashews).',
            'Dinner: Grilled chicken breast with roasted Brussels sprouts and quinoa.',
          ],
        },
        {
          day: 'Thursday',
          meals: [
            'Breakfast: Smoothie with spinach, banana, almond milk, and a scoop of protein powder.',
            'Lunch: Chickpea and vegetable wrap with tahini sauce in a whole wheat tortilla.',
            'Snack: Cottage cheese with pineapple chunks.',
            'Dinner: Baked cod with asparagus and a quinoa pilaf.',
          ],
        },
        {
          day: 'Friday',
          meals: [
            'Breakfast: Greek yogurt parfait with granola and sliced strawberries.',
            'Lunch: Grilled vegetable and mozzarella panini on whole-grain bread.',
            'Snack: Rice cakes with almond butter.',
            'Dinner: Shrimp stir-fry with brown rice and mixed vegetables in a teriyaki sauce.',
          ],
        },
        {
          day: 'Saturday',
          meals: [
            'Breakfast: Whole-grain pancakes with a side of mixed berries.',
            'Lunch: Spinach and feta stuffed chicken breast with a side of steamed broccoli.',
            'Snack: Rice crackers with guacamole.',
            'Dinner: Lentil and vegetable curry with brown rice.',
          ],
        },
      ];

  return (
    <ScrollView contentContainerStyle={styles.container}>
      {days.map((dayData, index) => (
        <View key={index} style={styles.dayContainer}>
          <Text style={styles.dayText}>{dayData.day}:</Text>
          {dayData.meals.map((meal, mealIndex) => (
            <Text key={mealIndex} style={styles.mealText}>{meal}</Text>
          ))}
        </View>
      ))}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    padding: 20,
    backgroundColor: '#F5F5F5',
  },
  dayContainer: {
    marginBottom: 20,
  },
  dayText: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  mealText: {
    fontSize: 16,
    marginLeft: 10,
  },
});

export default MenuScreen;
